import React, {Component} from 'react';
import {Tabs, Tab, Grid, Cell, Card, CardTitle, CardText, CardActions, Button, CardMenu, IconButton} from 'react-mdl';

class Projects extends Component  {
  constructor(props){
    super(props);
    this.state = { activeTab: 0 };
  }

  toggleCategories() {
    if(this.state.activeTab === 0 ) {
      return(
        <div className="projects-grid">
        {/* project 1 */}
        <Card shadow={5} style={{minWidth:'450', mergin: 'auto'}}>
          <CardTitle style={{color: '#fff', height: '176px', background: 'url(https://raddevon.com/wp-content/uploads/2018/10/react.jpg) center/ cover'}}>DoNation</CardTitle>
          <CardText>
          hello
          </CardText>
          <CardActions border>
            <Button href="https://github.com/tanuka16/DoNation-Back-end" colored>GitHub</Button>
            <Button href="https://youtu.be/RnQherXmMkk" colored>Live Demo</Button>
          </CardActions>
          <CardMenu style={{color:'#fff'}}>
            <IconButton name="share" />
          </CardMenu>
        </Card>

        {/* project 2 */}
        <Card shadow={5} style={{minWidth:'450', mergin: 'auto'}}>
          <CardTitle style={{color: '#fff', height: '176px', background: 'url(https://raddevon.com/wp-content/uploads/2018/10/react.jpg) center/ cover'}}>React Project</CardTitle>
          <CardText>
          hello
          </CardText>
          <CardActions border>
            <Button colored>GitHub</Button>
            <Button colored>Live Demo</Button>
          </CardActions>
          <CardMenu style={{color:'#fff'}}>
            <IconButton name="share" />
          </CardMenu>
        </Card>

        {/* project 3 */}
        <Card shadow={5} style={{minWidth:'450', mergin: 'auto'}}>
          <CardTitle style={{color: '#fff', height: '176px', background: 'url(https://raddevon.com/wp-content/uploads/2018/10/react.jpg) center/ cover'}}>React Project</CardTitle>
          <CardText>
          hello
          </CardText>
          <CardActions border>
            <Button colored>GitHub</Button>
            <Button colored>Live Demo</Button>
          </CardActions>
          <CardMenu style={{color:'#fff'}}>
            <IconButton name="share" />
          </CardMenu>
        </Card>
        </div>
      )
    }else if(this.state.activeTab === 1){
      return (
        <div><h1>This is Rails</h1></div>
      )
    }
  }


  render (){
    return (
      <div className="tabs">
        <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({ activeTab: tabId })} ripple>
          <Tab>React</Tab>
          <Tab>Rails</Tab>
          <Tab>React</Tab>
        </Tabs>

        <section className="projects-grid">
          <Grid>
            <Cell col={12}>
              <div className="content">{this.toggleCategories()}</div>
            </Cell>
          </Grid>
        </section>

      </div>
    )
  }
}

export default Projects;
